import Reacrt, {useState} from 'react'
import NewLineTable from "../NewLineTable/NewLineTable";
import s from './MainData.module.css'
import Filters from "./Filters/Filters";
import SearchData from "../SearchData/SearchData";
import React from "react";

const sortWithFilters = (arr, filters, search) => {
    if (search) {
        let searchKey
        for (const filtersKey in search) {
            searchKey = search[filtersKey]
        }
        debugger
        arr = arr.map(e => e[searchKey].toString().indexOf(search[searchKey]))
    }
    for (const mainDataKey in filters) {
        if (filters[mainDataKey]) {
            arr.sort(function (a, b) {
                if (mainDataKey === 'id') {
                    let x = a[mainDataKey];
                    let y = b[mainDataKey];
                    return x < y ? -1 : x > y ? 1 : 0
                } else if (mainDataKey === 'address') {
                    let x = a[mainDataKey]['streetAddress'].toLowerCase();
                    let y = b[mainDataKey]['streetAddress'].toLowerCase();
                    return x < y ? -1 : x > y ? 1 : 0;
                } else {
                    let x = a[mainDataKey].toLowerCase();
                    let y = b[mainDataKey].toLowerCase();
                    return x < y ? -1 : x > y ? 1 : 0;
                }
            });
        } else {
            arr.sort(function (a, b) {
                if (mainDataKey === 'id') {
                    let x = a[mainDataKey];
                    let y = b[mainDataKey];
                    return x > y ? -1 : x < y ? 1 : 0
                } else if (mainDataKey === 'address') {
                    let x = a[mainDataKey]['streetAddress'].toLowerCase();
                    let y = b[mainDataKey]['streetAddress'].toLowerCase();
                    return x > y ? -1 : x < y ? 1 : 0;
                } else {
                    let x = a[mainDataKey].toLowerCase();
                    let y = b[mainDataKey].toLowerCase();
                    return x > y ? -1 : x < y ? 1 : 0;
                }
            });
        }
    }
}

const MainData = (props) => {
    let [num, setNum] = useState(0)
    let [nowPage, setNowPage] = useState(1)
    let [filters, setNowFilters] = useState({id: true})
    let [search, setSearch] = useState(false)
    const firsLine = {
        id: "id",
        firstName: "firstName",
        lastName: "lastName",
        email: "email",
        phone: "phone",
        address: "address",
        description: "description"
    }

    const changeState = (e) => () => {
        setNowPage(e)
        setNum(e - 1)
    }

    if (props.data) {
        if (props.data.data.length <= 50) {
            let arr = props.data.data
            sortWithFilters(arr, filters, search)
            arr = arr.map(tr => (<tr><NewLineTable data={tr} key={tr.id}/></tr>))
            return (
                <div>
                    <SearchData selectData={firsLine} setSearch={setSearch}/>
                    <table>
                        <Filters filter={filters} setFilters={setNowFilters} data={firsLine}/>
                        {arr}
                    </table>
                </div>
            )
        } else {
            let arr = props.data.data
            sortWithFilters(arr, filters, search)
            arr = arr.slice(num * 50, (num + 1) * 50).map(tr => <tr><NewLineTable data={tr} key={tr.id}/></tr>)
            let nums = []
            let end = Math.ceil(props.data.data.length / 50)
            for (let i = 1; i < end + 1; i++) {
                nums.push(<span onClick={changeState(i)}
                                className={i === nowPage ? s.pagenatorNow : s.pagenator}>{i}</span>)
            }
            return (
                <div>
                    <SearchData/>
                    <table>
                        <Filters filter={filters} setFilters={setNowFilters} data={firsLine}/>
                        {arr}
                    </table>
                    <div>{nums}</div>
                </div>
            )
        }
    } else {
        return <div></div>
    }
}


export default MainData
