import Reacrt from 'react'
import s from './Filters.module.css'

const Filters = (props) => {

    const data = []

    const setNewFilter = (key) => () => {
        switch (key){
            case "id":
                props.setFilters({id: props.filter ? false : true})
                break
            case "firstName":
                props.setFilters({firstName: props.filter ? false : true})
                break
            case "lastName":
                props.setFilters({lastName: props.filter ? false : true})
                break
            case "id":
                props.setFilters({key: props.filter ? false : true})
                break
        }
    }

    for (let key in props.data) {
        data.push(<td onClick={setNewFilter(key)} className={s.column}>{props.data[key]}</td>)
    }
    return (
        <tr>
            {data}
        </tr>
    )
}

export default Filters
