import React from 'react'
import {connect} from "react-redux";
import {addSmallDataActionCreator} from "../../Redux/smallDataReduser";
import {addBigDataActionCreator} from "../../Redux/bigDataReduser";
import {loadBigDataApi, loadSmallDataApi} from "../../API/Api";
import s from './MainPage.module.css'
import MainData from "../MainData/MainData";

const MainPage = (props) => {

    const loadSmallData = async () => {
        const data = await loadSmallDataApi()
        props.addSmallDataActionCreator(data)
        props.addBigDataActionCreator(false)
    }

    const loadBigData = async () => {
        const data = await loadBigDataApi()
        props.addBigDataActionCreator(data)
        props.addSmallDataActionCreator(false)
    }
    return (
        <div className={s.buttonBlock}>
            <button onClick={loadSmallData}>Load small data</button>
            <button onClick={loadBigData}>Load big data</button>
            <MainData data={props.smallData.length === 0 ? props.bigData : props.smallData}/>
        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        smallData: state.smallData,
        bigData: state.bigData
    }
}

export default connect(mapStateToProps, {addSmallDataActionCreator, addBigDataActionCreator})(MainPage)
