import React from 'react'
import {connect} from "react-redux";
import {addSmallDataActionCreator} from "../../Redux/smallDataReduser";
import {addBigDataActionCreator} from "../../Redux/bigDataReduser";
import {loadSmallDataApi} from "../../API/Api";

const MainPage = (props) => {

    const loadSmallData = async () => {
        const data = await loadSmallDataApi()
        props.addSmallDataActionCreator(data)
    }

    return (
        <div>
            <button onClick={loadSmallData}>Load small data</button>
            <button>Load big data</button>
        </div>
    )
}


const mapStateToProps = (state) => {
    return {
        smallData: state.smallData,
        bigData: state.bigData
    }
}

export default connect(mapStateToProps, {addSmallDataActionCreator, addBigDataActionCreator})(MainPage)
