const ADD_BIG_DATA = 'ADD_BIG_DATA'





let initialState = []

export const bigDataReduser = (state, action) => {
    if (action.type === ADD_BIG_DATA) {
       return (
           [
               ...state,
               action.bigData
           ]
       )
    }
    return state
}

export const addBigDataActionCreator = (bigData) => {
    return {type: ADD_BIG_DATA, bigData: bigData}
}
