import React, {useState} from 'react'
import s from './AddForm.module.css'
import FormInputField from "./FormInputField/FormInputField";

const AddForm = (props) => {
    let id = React.createRef();
    let firstName = React.createRef();
    let lastName = React.createRef();
    let email = React.createRef();
    let phone = React.createRef();
    let streetAddress = React.createRef();
    let city = React.createRef();
    let state = React.createRef();
    let zip = React.createRef();
    let description = React.createRef();
    let [disable, setDisable] = useState(false)
    return (
        <div className={s.borderBlock}>
            <div className={!props.view ? s.hide : s.tableDiv}>
                <div></div>
                <FormInputField  className={s.inputField} lable={"id"} placeholder={"write id..."} linkRef={id}
                                type={"number"}/>
                <FormInputField className={s.inputField} lable={"first name"} placeholder={"write first name..."}
                                linkRef={firstName} type={"text"}/>
                <FormInputField className={s.inputField} lable={"last name"} placeholder={"write last name..."}
                                linkRef={lastName} type={"text"}/>
                <FormInputField className={s.inputField} lable={"email"} placeholder={"write email..."} linkRef={email}
                                type={"text"}/>
                <FormInputField className={s.inputField} lable={"phone"} placeholder={"write phone..."} linkRef={phone}
                                type={"number"}/>
                <FormInputField className={s.inputField} lable={"street address"} placeholder={"write street..."}
                                linkRef={streetAddress} type={"text"}/>
                <FormInputField className={s.inputField} lable={"city"} placeholder={"write city..."} linkRef={city}
                                type={"text"}/>
                <FormInputField className={s.inputField} lable={"state"} placeholder={"write state..."} linkRef={state}
                                type={"text"}/>
                <FormInputField className={s.inputField} lable={"zip"} placeholder={"write zip..."} linkRef={zip}
                                type={"number"}/>
                <FormInputField className={s.inputField} lable={"description"} placeholder={"write description..."}
                                linkRef={description} type={"text"}/>
                <div></div>
            </div>
            <button disabled={disable}>Add item</button>
        </div>
    )
}
export default AddForm
