import Reacrt, {useState} from 'react'
import NewLineTable from "../NewLineTable/NewLineTable";
import s from './MainData.module.css'
import Filters from "./Filters/Filters";


const MainData = (props) => {
    let [num, setNum] = useState(0)
    let [nowPage, setNowPage] = useState(1)
    let [filters, setNowFilters] = useState({
        id: true,
        firstName: false,
        lastName: false,
        email: false,
        phone: false,
        address: false,
        description: false
    })
    const firsLine = {
        id: "id",
        firstName: "firstName",
        lastName: "lastName",
        email: "email",
        phone: "phone",
        address: "address",
        description: "description"
    }

    const changeState = (e) => () => {
        setNowPage(e)
        setNum(e - 1)
    }

    if (props.data) {
        if (props.data.data.length <= 50) {
            let arr = props.data.data
            for (const mainDataKey in filters) {
                if (filters[mainDataKey]) {
                    arr.sort(function(a,b) {
                        if (mainDataKey === 'id') {
                            let x = a[mainDataKey];
                            let y = b[mainDataKey];
                            return x < y ? 1 : 0
                        }
                        let x = a[mainDataKey].toLowerCase();
                        let y = b[mainDataKey].toLowerCase();
                        return x < y ? -1 : x > y ? 1 : 0;
                    });
                }
            }
            debugger



            arr.map(tr => (<tr><NewLineTable data={tr} key={tr.id}/></tr>))
            return (
                <table>
                    <Filters data={firsLine}/>
                    {arr}
                </table>
            )
        } else {
            let arr = props.data.data.slice(num * 50, (num + 1) * 50).map(tr => (
                <tr><NewLineTable data={tr} key={tr.id}/></tr>))
            let nums = []
            let end = Math.ceil(props.data.data.length / 50)
            for (let i = 1; i < end + 1; i++) {
                nums.push(<span onClick={changeState(i)}
                                className={i === nowPage ? s.pagenatorNow : s.pagenator}>{i}</span>)
            }
            return (
                <div>
                    <table>
                        <Filters data={firsLine}/>
                        {arr}
                    </table>
                    <div>{nums}</div>
                </div>
            )
        }
    } else {
        return <div></div>
    }
}


export default MainData
